#!/usr/bin/env node

/**
 * Bundle acorn parser into src/parser.js for zero runtime dependencies
 * This creates a self-contained parser module with acorn bundled inside
 */

import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const projectRoot = path.resolve(__dirname, '..');
const acornPath = path.join(projectRoot, 'node_modules/acorn/dist/acorn.mjs');
const outputPath = path.join(projectRoot, 'src/parser.js');

console.log('ðŸ“¦ Bundling Acorn parser...');
console.log(`   Input: ${acornPath}`);
console.log(`   Output: ${outputPath}`);

// Read acorn source
let acornSource = fs.readFileSync(acornPath, 'utf8');

// acorn.mjs already has exports at the bottom, just add a header comment
const bundledCode = `/**
 * Self-contained Acorn parser (bundled for zero runtime dependencies)
 * Acorn is MIT licensed: https://github.com/acornjs/acorn
 * This file is auto-generated by scripts/bundle-acorn.js
 * Original size: ~${Math.round(acornSource.length / 1024)}KB
 */

${acornSource}
`;

// Write bundled file
fs.writeFileSync(outputPath, bundledCode, 'utf8');

const outputSize = (bundledCode.length / 1024).toFixed(1);
console.log(`âœ… Bundled successfully! Output size: ${outputSize}KB`);
console.log('   Zero runtime dependencies achieved!');

